(()=>{"use strict";const i=new class{constructor(i,t){this.name=i,this.length=t,this.hits=[]}hit(i){this.hits.push(i)}isSunk(){return this.hits.length===this.length}}("test",4);i.hit(2),i.hit(1),i.hit(4),i.hit(3);const t=new class{constructor(i){this.board=i||[],this.board.length||this.initialize()}initialize(){for(let i=0;i<100;i++)this.board.push({hasShip:!1,isShot:!1})}markHit(i){this.board[i].isShot=!0}checkHit(i){return this.board[i].hasShip}checkCollision(i,t,h){for(let h=0;h<i;h++)return this.board[t+10*h].hasShip}placeShip(i,t,h,s){if(!this.checkCollision(t,h,s))if(s="x")for(let i=0;i<t;i++)this.board[h+10*i].hasShip=!0;else if(s="y")for(let i=0;i<t;i++)this.board[h+i].hasShip=!0}};console.log(t),t.placeShip("test",4,5,"y"),console.log(t),console.log("placing new ship with collision"),t.placeShip("bluh",3,4,"x"),console.log(t.checkCollision(3,4,"x"))})();